def clear_window(window):
    for widget in window.winfo_children():
        widget.destroy()
    for attr in dir(window):
        if isinstance(getattr(window, attr), PhotoImage):
            setattr(window, attr, None)

def validate_login(window):
    username = window.entry_2.get().strip()
    password = window.entry_1.get().strip()
    print(f"Username entered: '{username}'")
    print(f"Password entered: '{password}'")
    if username == "admin" and password == "password":
        clear_window(window)
        from main_menu.build.gui import open_main_menu
        open_main_menu(window)
        
    else:
        messagebox.showerror("Invalid Credentials", "Incorrect username or password.")

def open_login_page():
    window = Tk()
    window.geometry("1200x720")
    window.configure(bg = "#FFFFFF")
    populate_window(window)
    window.resizable(False, False)
    window.mainloop()

